SELECT FORMAT(MIN(s.subscription_price), 2) AS faturamento_minimo, FORMAT(MAX(s.subscription_price), 2) AS faturamento_maximo, FORMAT(AVG(subscription_price), 2) AS faturamento_medio, FORMAT(SUM(subscription_price), 2) AS faturamento_total FROM SpotifyClone.subscription AS s INNER JOIN SpotifyClone.user AS u ON s.subscription_id=u.subscription;
